function handle_path(e){jQuery.ajax({type:"POST",url:"/package/image_path.json",data:{key:e.product_key,version:e.product_version,scope:e.scope},dataType:"text",success:function(t){"nil"==t||show_pinit_button(t,e)}})}function upload_file(e){canvas=document.getElementById(e.canvas_id),canvas&&e.pinit&&jQuery.ajax({type:"POST",url:"/package/upload_image.json",data:{image:document.getElementById(e.canvas_id).toDataURL(),key:e.product_key,version:e.product_version,scope:e.scope},dataType:"text",success:function(t){show_pinit_button(t,e)}})}function show_pinit_button(e,t){if(0!=t.pinit){if(0==t.resize&&t.data_length>t.data_border){canvas_container=document.getElementById(t.container_id);var n=document.createElement("IMG");n.src=e,n.style.width="600px",canvas_container.appendChild(n)}pin_button=document.getElementById("pinit"),pin_button.href="https://pinterest.com/pin/create/button/?url=https%3A%2F%2Fwww.versioneye.com/package/"+t.product_key+"/"+t.product_version+"&media="+e+"&description="+t.product_name+" : "+t.version+" : "+t.scope,pin_button.style.display="block"}}